import './Footer.css'

const Footer = props => {
    const completedTodoCount = props.todos.reduce((pre, todo) => {return pre + (todo.isDone? 1: 0)}, 0)
    const total = props.todos.length

    const handleCheckAll = event => {
        props.deleteAllCompleted()
    }
}
